import React, {useState} from 'react';
import axios from "axios";

const EditAward = ({ data, setEditAward, isClicked }) => {
    const [isDeleted, setIsDeleted]         = useState(false);
    const [award, setAward]                 = useState(data.awards);
    const [awardDetail,setAwardDetail]      = useState(data.awards_details);
    const award_id                          = data.id;

    const requestSaveAward = async () => {
        try {
            await axios.post(
                'http://localhost:1234/edit/award',
                { award, awardDetail, award_id }
            );
        } catch (e) {
            console.log(e);
        }
    }

    const requestDeleteAward = async () => {
        try {
            await axios.post(
                'http://localhost:1234/delete/award',
                { award_id }
            );
            
            setIsDeleted(false);
            console.log(isDeleted);
        } catch (e) {
            console.log(e);
        }
    }

    if (isClicked){
        requestSaveAward();
        setEditAward(false);
    }
    if (isDeleted){
        requestDeleteAward();
        setEditAward(false);
    }
    const handleDeleteAward = (e) => {
        e.preventDefault();
        e.stopPropagation();
        setIsDeleted(prevState => (!prevState));
    }

    return (
        <div key={`award_${award_id}_edit`} >
            <main className="form-input">
                <div className="form-floating">
                    <input type="text" className="form-control"  placeholder="수상 이름" defaultValue={award} onChange={e => setAward(e.target.value)} />
                    <label htmlFor="floatingInput">수상 이름</label>
                </div>
                <div className="form-floating">
                    <input type="text" className="form-control" placeholder="수상 내용" defaultValue={awardDetail} onChange={e => setAwardDetail(e.target.value)} />
                    <label htmlFor="floatingInput">수상 내용</label>
                </div>
                <div className="btn-group" role="group" aria-label="Basic outlined example">
                    <button className="btn btn-outline-primary" onClick={handleDeleteAward} type="button">삭제</button>
                </div>
            </main>
        </div>
    );
    
}
const Awards = ({ awardData, editAward, setEditAward, handleEditAwards, isClicked, setIsClicked }) => {
    const handleAwardSubmit = (e) => {
        e.preventDefault();
        e.stopPropagation();
        setIsClicked(prevState => (!prevState));
    }
    return (
        <div>
            <div className="awards-portfolio">
                <h3>수상이력</h3>
                {
                    editAward ?
                    Array.from(awardData).map(award => {
                        return (
                            <EditAward data={award} setEditAward={setEditAward} isClicked={isClicked} />
                        )
                    }) : 
                    Array.from(awardData).map(award => {
                        return (
                            <div key={`award_${award.id}`}>
                                <h5>{award.awards}</h5>
                                <p>{award.awards_details}</p>
                            </div>
                            
                        )
                    })
                }
                {editAward ?
                    <div>
                        <div className="btn-group" role="group" aria-label="Basic outlined example">
                            <button className="btn btn-outline-primary" onClick={handleAwardSubmit} type="submit">완료</button>
                        </div>
                    </div>:
                    <div className="btn-group" role="group" aria-label="Basic outlined example">
                        <button className="btn btn-outline-primary" onClick={handleEditAwards} type="button">수정</button>
                    </div>
                }
            </div>
        </div>
    );
    
}

export default Awards;
