import React, {useState} from 'react';
import axios from "axios";

const Schools = ({ sessionId, schoolData, editStatus, handleEditSchools }) => {
    const [name, setName] = useState(schoolData.name);
    const [major, setMajor] = useState(schoolData.major);
    const [status, setStatus] = useState(schoolData.status);

    // 백앤드로 데이터 전송 및 응답 받기
    const requestSave = async (school_id) => {
        try {
            await axios.post(
                'http://localhost:1234/edit/school',
                { sessionId, name, major, status, school_id }
            );
        } catch (e) {
            console.log(e);
        }
    }

    const handleSignUpSubmit = (e, school_id) => {
        e.preventDefault();
        requestSave(school_id);
    }

    return (
        <div>
            <div className="school-portfolio">
                <h3>학력</h3>
                {
                    editStatus ?
                    Array.from(schoolData).map(school => {
                        return (
                            <div key={`school_${school.id}`}>
                                <main className="form-input">
                                    <form onSubmit={(e, school_id) =>handleSignUpSubmit(e, school_id)}>
                                        <div className="form-floating">
                                            <input name="user_id" type="text" className="form-control" id="floatingInput" placeholder="학교이름" defaultValue={school.name} onChange={e => setName(e.target.value)} />
                                            <label htmlFor="floatingInput">학교이름</label>
                                        </div>
                                        <div className="form-floating">
                                            <input name="user_id" type="text" className="form-control" id="floatingInput" placeholder="전공" defaultValue={school.major} onChange={e => setMajor(e.target.value)} />
                                            <label htmlFor="floatingInput">전공</label>
                                        </div>
                                        <div className="form-floating">
                                            <input name="name" type="text" className="form-control" id="floatingPassword" placeholder="상태" defaultValue={school.status} onChange={e => setStatus(e.target.value)} />
                                            <label htmlFor="floatingInput">상태</label>
                                        </div>
                                        <div>
                                            <div className="btn-group" role="group" aria-label="Basic outlined example">
                                                <button className="btn btn-outline-primary" onClick={handleEditSchools} type="submit">완료</button>
                                            </div>
                                        </div>
                                        
                        
                                    </form>
                                </main>
                            </div>
                        )
                    }) : 
                    Array.from(schoolData).map(school => {
                        return (
                            <div key={`school_${school.id}`}>
                                <h5>{school.name}</h5>
                                <h6>{school.major}</h6>
                                <p>{school.status}</p>
                                <div className="btn-group" role="group" aria-label="Basic outlined example">
                                    <button className="btn btn-outline-primary" onClick={handleEditSchools} type="button">수정</button>
                                </div>
                            </div>
                            
                        )
                    })
                }
            </div>
        </div>
    );
    
}

export default Schools;
