import React, { useState, useEffect } from 'react';
import { Redirect } from 'react-router-dom';
import axios from "axios";

import Awards from "../components/portfolio/Awards";
import Schools from "../components/portfolio/Schools";
import Projects from "../components/portfolio/Projects";
import Certificates from "../components/portfolio/Certificates";
import Profile from "../components/portfolio/Profile";
import { FullPage, LeftSide, RightSide } from '../styles/MainStyle';

const Main = ({ sessionId }) => {
    const templateStyle                             = {width: '60vw'}
    const [awardData, setAwardData]                 = useState({});
    const [schoolData, setSchoolData]               = useState({});
    const [certificateData, setCertificateData]     = useState({});
    const [projectData, setProjectData]             = useState({});
    const [profileData, setProfileData]             = useState({});
    const [editProfile, setEditProfile]             = useState(false);
    const [editSchool, setEditSchool]               = useState(false);
    const [editAward, setEditAward]                 = useState(false);
    const [editProject, setEditProject]             = useState(false);
    const [editCertificate, setEditCertificate]     = useState(false);

    
    // get Data from Flask
    useEffect(()=>{
        const fetchPortfolio = async () => {
            const res = await axios.post('http://localhost:1234/portfolio/main', {sessionId});
            const data = res.data.data.portfolio;
            setAwardData(data.awards);
            setSchoolData(data.schools);
            setCertificateData(data.certificates);
            setProjectData(data.projects);
            setProfileData(data.profile);
        }

        fetchPortfolio();
    }, [sessionId])

    // Set the "Edit<->Read" State's status 
    const handleEditProfile         = () => {setEditProfile(prevState => ({profile : !prevState.profile}))};
    const handleEditAwards          = () => {setEditAward(prevState => ({award : !prevState.award}))};
    const handleEditSchools         = () => {setEditSchool(prevState => ({school : !prevState.school}))};
    const handleEditCertificates    = () => {setEditCertificate(prevState => ({certificate : !prevState.certificate}))};
    const handleEditProjects         = () => {setEditProject(prevState => ({project : !prevState.project}))};


    
    return (
        
        <FullPage>
            {sessionId === null && <Redirect to="/login" />}
            <h1 className="text-center">My Protfolio</h1>
            <br />
            <LeftSide>
                <div className="card text-center" style={{width: '28vw'}}>
                    <div className="card-body">
                        <Profile profileData={profileData} />
                        {editProfile ?
                        <div>
                            <div className="btn-group" role="group" aria-label="Basic outlined example">
                                <button className="btn btn-outline-primary" onClick={handleEditProfile} type="submit">완료</button>
                            </div>
                        </div>:
                        <div className="btn-group" role="group" aria-label="Basic outlined example">
                            <button className="btn btn-outline-primary" onClick={handleEditProfile} type="button">수정</button>
                        </div>
                        }
                    </div>
                </div>
            </LeftSide>
            <RightSide>
                <div className="card text-center" style={templateStyle}>
                    <div className="card-body">
                        <Schools sessionId={sessionId} schoolData={schoolData} editSchool={editSchool} setEditSchool={setEditSchool} />
                    </div>
                </div> 

                <div className="card text-center" style={templateStyle}>
                    <div className="card-body">
                        <Awards awardData={awardData} />
                        {editAward ?
                        <div>
                            <div className="btn-group" role="group" aria-label="Basic outlined example">
                                <button className="btn btn-outline-primary" onClick={handleEditAwards} type="submit">완료</button>
                            </div>
                        </div>:
                        <div className="btn-group" role="group" aria-label="Basic outlined example">
                            <button className="btn btn-outline-primary" onClick={handleEditAwards} type="button">수정</button>
                        </div>
                        }
                    </div>
                </div>

                <div className="card text-center" style={templateStyle}>
                    <div className="card-body">
                        <Projects projectData={projectData} />
                        {editProject ?
                        <div>
                            <div className="btn-group" role="group" aria-label="Basic outlined example">
                                <button className="btn btn-outline-primary" onClick={handleEditProjects} type="submit">완료</button>
                            </div>
                        </div>:
                        <div className="btn-group" role="group" aria-label="Basic outlined example">
                            <button className="btn btn-outline-primary" onClick={handleEditProjects} type="button">수정</button>
                        </div>
                        }
                    </div>
                </div>

                <div className="card text-center" style={templateStyle}>
                    <div className="card-body">
                        <Certificates certificateData={certificateData} />
                        {editCertificate ?
                        <div>
                            <div className="btn-group" role="group" aria-label="Basic outlined example">
                                <button className="btn btn-outline-primary" onClick={handleEditCertificates} type="submit">완료</button>
                            </div>
                        </div>:
                        <div className="btn-group" role="group" aria-label="Basic outlined example">
                            <button className="btn btn-outline-primary" onClick={handleEditCertificates} type="button">수정</button>
                        </div>
                        }
                    </div>
                </div>
            </RightSide>
        </FullPage>
    );
    
}

export default Main;
