import React, {useState} from 'react';
import axios from "axios";

const Schools = ({ sessionId, schoolData, editStatus, handleEditSchools }) => {
    const [name, setName] = useState('');
    const [major, setMajor] = useState('');
    const [status, setStatus] = useState('');

    // 백앤드로 데이터 전송 및 응답 받기
    const requestSave = async () => {
        try {
            const res = await axios.post(
                'http://localhost:1234/edit/school',
                { sessionId, name, major, status }
            );
            console.log(res.data)
            alert(res.data.message);  
        } catch (e) {
            console.log(e);
        }
    }

    const handleSignUpSubmit = (e) => {
        e.preventDefault();
        requestSave();
    }

    return (
        <div>
            <div className="school-portfolio">
                <h3>학력</h3>
                {
                    editStatus ?
                    Array.from(schoolData).map(school => {
                        return (
                            <div key={`school_${school.id}`}>
                                <input type="text" name="name" placeholder="학교이름" defaultValue={school.name} onChange={e => setName(e.target.value)} />
                                <input type="text" name="major" placeholder="전공" defaultValue={school.major} onChange={e => setMajor(e.target.value)} />
                                <input type="text" name="status" placeholder="상태" defaultValue={school.status} onChange={e => setStatus(e.target.value)} />
                            </div>
                        )
                    }) : 
                    Array.from(schoolData).map(school => {
                        return (
                            <div key={`school_${school.id}`}>
                                <h5>{school.name}</h5>
                                <h6>{school.major}</h6>
                                <p>{school.status}</p>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    );
    
}

export default Schools;
