import React, {useState} from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [sessionId, setSessionId] = useState(null);
    const history = useHistory();
    
    const requestLogin = async () => {
        try {
            const res = await axios.post(
                'http://localhost:1234/auth/login',
                { email, password }
            );
            console.log(res.data)
            const user_id = res.data.data.user_data.id;
            setSessionId(user_id);
            window.sessionStorage.setItem("id", user_id);
            history.push("/main");
        } catch (e) {
            console.log(e);
        }
    }

    const handleLoginSubmit = (e) => {
        e.preventDefault();
        requestLogin();
    }

        return (
            <section>
                <form  onSubmit={e =>handleLoginSubmit(e)}>
                    <div className="mb-3">
                        <label htmlFor="exampleInputEmail1" className="form-label">아이디</label>
                        <input type="email" name="user_id" id="user_id" aria-describedby="emailHelp" placeholder="아이디 입력" onChange={e => setEmail(e.target.value)} />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="exampleInputPassword1" className="form-label">비밀번호</label>
                        <input type="password" placeholder="비밀번호 입력" onChange={e => setPassword(e.target.value)} />
                    </div>
                    <button type="submit" className="btn btn-secondary">Submit</button>
                    <br />
                    <div className="mb-3">
                        <button type="button" className="btn btn-link" onClick={() => history.push("/signup")}>회원가입하기</button>
                    </div>
                </form>
            </section>
            
        );
}

export default Login;
