import React, { useState, useEffect } from 'react';
import { Redirect } from 'react-router-dom';
import axios from "axios";

import Awards from "../components/portfolio/Awards";
import Schools from "../components/portfolio/Schools";
import Projects from "../components/portfolio/Projects";
import Certificates from "../components/portfolio/Certificates";
import Profile from "../components/portfolio/Profile";
import { FullPage, LeftSide, RightSide } from '../styles/MainStyle';

const Main = ({ sessionId }) => {
    const templateStyle                                     = {width: '60vw'}
    const [awardData, setAwardData]                         = useState({});
    const [schoolData, setSchoolData]                       = useState({});
    const [certificateData, setCertificateData]             = useState({});
    const [projectData, setProjectData]                     = useState({});
    const [profileData, setProfileData]                     = useState({});

    const [editProfile, setEditProfile]                     = useState(false);
    const [editSchool, setEditSchool]                       = useState(false);
    const [editAward, setEditAward]                         = useState(false);
    const [editProject, setEditProject]                     = useState(false);
    const [editCertificate, setEditCertificate]             = useState(false);

    const [isSchoolClicked, setIsSchoolClicked]             = useState(false);
    const [isAwardClicked, setIsAwardClicked]               = useState(false);
    const [isCertificateClicked, setIsCertificateClicked]   = useState(false);
    const [isProjectClicked, setIsProjectClicked]           = useState(false);
    const [isProfileClicked, setIsProfileClicked]           = useState(false);

    const [isSchoolDeleted, setIsSchoolDeleted]             = useState(false);
    const [isAwardDeleted, setIsAwardDeleted]               = useState(false);
    const [isCertificateDeleted, setIsCertificateDeleted]   = useState(false);
    const [isProjectDeleted, setIsProjectDeleted]           = useState(false);
    const [isProfileDeleted, setIsProfileDeleted]           = useState(false);
    
    // get Data from Flask
    useEffect(()=>{
        // Default roaded data
        const fetchPortfolio = async () => {
            const res = await axios.post('http://localhost:1234/portfolio/main', {sessionId});
            const data = res.data.data.portfolio;
            setAwardData(data.awards);
            setSchoolData(data.schools);
            setCertificateData(data.certificates);
            setProjectData(data.projects);
            setProfileData(data.profile);
        }
        fetchPortfolio();

        // After edit reroad data
        const fetchProfile = async () => {
            const res = await axios.post('http://localhost:1234/portfolio/main/profile', {sessionId});
            const data = res.data.data.profile;
            setProfileData(data);
            setIsProfileClicked(false);
            
        }
        const fetchAwards = async () => {
            const res = await axios.post('http://localhost:1234/portfolio/main/awards', {sessionId});
            const data = res.data.data.awards;
            setAwardData(data);
            if(mode === 1) {setIsAwardClicked(false);}
            if(mode === 2) {setIsAwardDeleted(false);}
            mode = 0;
        }
        const fetchSchools = async () => {
            const res = await axios.post('http://localhost:1234/portfolio/main/schools', {sessionId});
            const data = res.data.data.schools;
            setSchoolData(data);
            if(mode === 1) {setIsSchoolClicked(false);}
            if(mode === 2) {setIsSchoolDeleted(false);}
            mode = 0;
        }
        const fetchCertificates = async () => {
            const res = await axios.post('http://localhost:1234/portfolio/main/certificates', {sessionId});
            const data = res.data.data.certificates;
            setCertificateData(data);
            if(mode === 1) {setIsCertificateClicked(false);}
            if(mode === 2) {setIsCertificateDeleted(false);}
            mode = 0;
        }
        const fetchProjects = async () => {
            const res = await axios.post('http://localhost:1234/portfolio/main/projects', {sessionId});
            const data = res.data.data.projects;
            setProjectData(data);
            if(mode === 1) {setIsProjectClicked(false);}
            if(mode === 2) {setIsProjectDeleted(false);}
            mode = 0;
        }
        const mode = 0;
        if(editSchool && isSchoolClicked){
            mode = 1;
            fetchSchools();
        }
        if(editAward && isAwardClicked){
            mode = 1;
            fetchAwards();
        }
        if(editCertificate && isCertificateClicked){
            mode = 1;
            fetchCertificates();
        }
        if(editProfile && isProfileClicked){
            fetchProfile();
        }
        if(editProject && isProjectClicked){
            mode = 1;
            fetchProjects();
        }
        
        if(editSchool && isSchoolDeleted){
            mode = 2;
            fetchSchools();
        }
        if(editAward && isAwardDeleted){
            mode = 2;
            fetchAwards();
        }
        if(editCertificate && isCertificateDeleted){
            mode = 2;
            fetchCertificates();
        }
        if(editProject && isProjectDeleted){
            mode = 2;
            fetchProjects();
        }

    }, [sessionId, editSchool, editAward, editCertificate, editProfile, editProject, 
        setSchoolData, setAwardData, setCertificateData, setProfileData, setProjectData, 
        isProjectClicked, isSchoolClicked, isAwardClicked, isProfileClicked, isCertificateClicked,
        setIsProjectClicked, setIsSchoolClicked, setIsAwardClicked, setIsProfileClicked, setIsCertificateClicked,
        isSchoolDeleted, isAwardDeleted, isCertificateDeleted, isProjectDeleted, isProfileDeleted,
        setIsSchoolDeleted, setIsAwardDeleted, setIsCertificateDeleted, setIsProjectDeleted, setIsProfileDeleted]);

    // Set the "Edit<->Read" State's status 
    const handleEditProfile         = () => {setEditProfile(prevState => (!prevState))};
    const handleEditAwards          = () => {setEditAward(prevState => (!prevState))};
    const handleEditSchools         = () => {setEditSchool(prevState => (!prevState))};
    const handleEditCertificates    = () => {setEditCertificate(prevState => (!prevState))};
    const handleEditProjects        = () => {setEditProject(prevState => (!prevState))};
    
    return (
        
        <FullPage>
            {sessionId === null && <Redirect to="/login" />}
            <h1 className="text-center">My Protfolio</h1>
            <br />
            <LeftSide>
                <div className="card text-center" style={{width: '28vw'}}>
                    <div className="card-body">
                        <Profile profileData={profileData} editProfile={editProfile} setEditProfile={setEditProfile} handleEditProfile={handleEditProfile} isClicked={isProfileClicked} setIsClicked={setIsProfileClicked} />
                    </div>
                </div>
            </LeftSide>
            <RightSide>
                <div className="card text-center" style={templateStyle}>
                    <div className="card-body">
                        <Schools schoolData={schoolData} editSchool={editSchool} setEditSchool={setEditSchool} handleEditSchools={handleEditSchools} isClicked={isSchoolClicked} setIsClicked={setIsSchoolClicked} />
                    </div>
                </div> 

                <div className="card text-center" style={templateStyle}>
                    <div className="card-body">
                        <Awards awardData={awardData} editAward={editAward} setEditAward={setEditAward} handleEditAwards={handleEditAwards} isClicked={isAwardClicked} setIsClicked={setIsAwardClicked} isDeleted={isAwardDeleted} setIsDeleted={setIsAwardDeleted} />
                    </div>
                </div>

                <div className="card text-center" style={templateStyle}>
                    <div className="card-body">
                        <Projects projectData={projectData} editProject={editProject} setEditProject={setEditProject} handleEditProjects={handleEditProjects} isClicked={isProjectClicked} setIsClicked={setIsProjectClicked} />
                    </div>
                </div>

                <div className="card text-center" style={templateStyle}>
                    <div className="card-body">
                        <Certificates certificateData={certificateData}  editCertificate={editCertificate} setEditCertificate={setEditCertificate} handleEditCertificates={handleEditCertificates} isClicked={isCertificateClicked} setIsClicked={setIsCertificateClicked}/>
                    </div>
                </div>
            </RightSide>
        </FullPage>
    );
    
}

export default Main;
