import React, {useState} from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const Login = ({ setSessionId }) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const history = useHistory();
    
    const requestLogin = async () => {
        try {
            const res = await axios.post(
                'http://localhost:1234/auth/login',
                { email, password }
            );
            const user_id = res.data.data.user_data.id;
            setSessionId(user_id);
            window.sessionStorage.setItem("id", user_id);
            history.push("/main");
        } catch (e) {
            console.log(e);
        }
    }

    const handleLoginSubmit = (e) => {
        e.preventDefault();
        requestLogin();
    }

        return (
            <div class="text-center">
                <main class="form-signin">
                    <form onSubmit={e =>handleLoginSubmit(e)}>
                        <h1 class="h3 mb-3 fw-normal">Please login</h1>
                        <div class="form-floating">
                            <input type="email" class="form-control" id="floatingInput" placeholder="name@example.com" onChange={e => setEmail(e.target.value)} />
                            <label for="floatingInput">Email address</label>
                        </div>
                        <div class="form-floating">
                            <input type="password" class="form-control" id="floatingPassword" placeholder="Password" onChange={e => setPassword(e.target.value)} />
                            <label for="floatingPassword">Password</label>
                        </div>
                        <button class="w-100 btn btn-lg btn-secondary" type="submit" onClick={() => history.push("/signup")}>Sign in</button>
                        <p class="mt-5 mb-3 text-muted">&copy; 2021 RacerIn Co. all rights reserved.</p>
                    </form>
                </main>
            </div>
        );
}

export default Login;
