import React, { useState } from 'react';
import axios from "axios";
import { useHistory } from 'react-router-dom';

const Signup = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [passwordCheck, setPasswordCheck] = useState("");
    const [name, setName] = useState("");
    const history = useHistory();

    const requestRegister = async () => {
        try {
            await axios.post(
                "http://localhost:1234/signup",
                { email, password, name }
            );
            history.push("/login");
        } catch (e) {
            alert(e.response.data.message);
        }
    }

    const handleRegisterSubmit = (e) => {
        e.preventDefault();
        requestRegister();
    }


        return (
            <div>
                <div>
                    <label for="user_id">아이디</label>
                    <input type="text" name="user_id" id="user_id" placeholder="이메일 입력" />
                </div>
                <div>
                    <label for="password">비밀번호</label>
                    <input name="password" type="password" placeholder="비밀번호 입력" />
                </div>
                <div>
                    <label for="password">비밀번호 확인</label>
                    <input name="check_password" type="password" placeholder="비밀번호 확인" />
                </div>
                <div>
                    <label for="name">이름</label>
                    <input name="name" type="name" placeholder="이름 입력" />
                </div>
                <input type="submit" value="회원가입" />
            </div>
        );
}

export default Signup;
