import React, { useState, useEffect } from 'react';
import axios from "axios";
import { useHistory, Redirect  } from 'react-router-dom';


const Main = ({ sessionId, setSessionId }) => {
    const [awardData, setAwardData] = useState({});
    const [schoolData, setSchoolData] = useState({});
    const [certificateData, setCertificateData] = useState({});
    const [projectData, setProjectsData] = useState({});
    const history = useHistory();

    const logoutRequest = async () => {
        try {
            await axios.get('http://localhost:1234/auth/logout');
            window.sessionStorage.clear();
            setSessionId(null);
            history.push("/login");
        }
        catch (e) {
            alert(e);
        }
    }
    useEffect(()=>{
        const fetchPortfolio = async () => {
            const res = await axios.post('http://localhost:1234/portfolio/main', {sessionId});
            const data = res.data.data.portfolio;

            setAwardData(data.awards);
            setSchoolData(data.schools);
            setCertificateData(data.certificates);
            setProjectsData(data.projects);
        }

        fetchPortfolio();
    }, [sessionId])
    return (
        <div>
            {sessionId === null && <Redirect to="/login" />}
            <h1>메인페이지</h1>
            <p><button onClick={() => {logoutRequest()}}>로그아웃</button></p>
            <div className="school-portfolio">
                <h3>학력</h3>
                {
                    Array.from(schoolData).map(school => {
                        return (
                            <div key={`school_${school.id}`}>
                                <p>{school.name}</p>
                                <p>{school.major}</p>
                                <p>{school.status}</p>
                            </div>
                        )
                    })
                }
            </div>
            <div className="awards-portfolio">
                <h3>수상이력</h3>
                {
                    Array.from(awardData).map(award => {
                        return (
                            <div key={`award_${award.id}`}>
                                <p>{award.awards}</p>
                                <p>{award.awards_details}</p>
                            </div>
                        )
                    })
                }
            </div>
            <div className="projects-portfolio">
                <h3>프로젝트</h3>
                {
                    Array.from(projectData).map(project => {
                        return (
                            <div key={`project_${project.id}`}>
                                <p>{project.project_name}</p>
                                <p>{project.project_details}</p>
                            </div>
                        )
                    })
                }
            </div>
            <div className="certificates-portfolio">
                <h3>자격증</h3>
                {
                    Array.from(certificateData).map(certificate => {
                        return (
                            <div key={`certificate_${certificate.id}`}>
                                <p>{certificate.cert_name}</p>
                                <p>{certificate.cert_from}</p>
                                <p>{certificate.cert_date}</p>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    );
    
}

export default Main;
