import React, {useState} from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';


const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const history = useHistory();
    
    const requestLogin = async () => {
        try {
            const res = await axios.post(
                "http://localhost:1234/login",
                { email, password }
            );
            console.log(res.data);
            window.sessionStorage.setItem("id", res.data.user_data.id);
            history.push("/main");
        } catch (e) {
            console.log(e);
        }
    }

    const handleLoginSubmit = (e) => {
        e.preventDefault();
        requestLogin();
    }

        return (
            <section>
                <form  onSubmit={e =>handleLoginSubmit(e)}>
                    <div class="mb-3">
                        <label htmlFor="exampleInputEmail1" class="form-label">아이디</label>
                        <input type="email" name="user_id" id="user_id" aria-describedby="emailHelp" placeholder="아이디 입력" onChange={e => setEmail(e.target.value)} />
                    </div>
                    <div class="mb-3">
                        <label htmlFor="exampleInputPassword1" class="form-label">비밀번호</label>
                        <input type="password" placeholder="비밀번호 입력" onChange={e => setPassword(e.target.value)} />
                    </div>
                    <button type="submit" class="btn btn-secondary">Submit</button>
                    <br />
                    <div class="mb-3">
                        <button type="button" class="btn btn-link" onClick={() => history.push("/signup")}>회원가입</button>
                    </div>
                </form>
            </section>
            
        );
}

export default Login;
