import React, { useState, useEffect } from 'react';
import axios from "axios";
import { useHistory } from 'react-router-dom';

const Signup = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [checkPassword, setCheckPassword] = useState("");
    const [name, setName] = useState("");
    const history = useHistory();
    const requestSignUp = async (email, password, name) => {
        try {
        
            const res = await axios.post(
                "http://localhost:1234/signup",
                { email, password, name }
            );
            console.log(res);
            history.push("/login");
        } catch (e) {
            console.log(e);
        }
    }

    const handleSignUpSubmit = (e) => {
        e.preventDefault();
        requestSignUp(email, password, name);
    }


        return (
            <div>
                <from onSubmit={handleSignUpSubmit}>
                    <div>
                        <label htmlFor="user_id">아이디</label>
                        <input type="text" name="user_id" id="user_id" placeholder="이메일 입력" onChange={e => setEmail(e.target.value)} />
                    </div>
                    <div>
                        <label htmlFor="password">비밀번호</label>
                        <input name="password" type="password" placeholder="비밀번호 입력" onChange={e => setPassword(e.target.value)} />
                    </div>
                    <div>
                        <label htmlFor="password">비밀번호 확인</label>
                        <input name="check_password" type="password" placeholder="비밀번호 확인" onChange={e => setCheckPassword(e.target.value)} />
                    </div>
                    <div>
                        <label htmlFor="name">이름</label>
                        <input name="name" type="name" placeholder="이름 입력" onChange={e => setName(e.target.value)} />
                    </div>
                    <input type="submit" value="회원가입" />
                </from>
            </div>
        );
}

export default Signup;
