import React, { useState } from 'react';
import axios from "axios";

const Main = () => {
    const [portfolio, setPortfolio] = useState();

    const fetchPortfolio = async () => {
        try {
            const response = await axios.get('http://localhost:5000/main');
            setPortfolio(response.data.data.portfolio);
        } catch (e) {
            console.log(e.response.data);
        }
    }
    fetchPortfolio();
    const { awards, schools, certificates, projects } = portfolio;
    
    return (
        <div>
            <h1>메인페이지</h1>
            <p><a class="nav-link" href="{{ url_for('logout_bp.logout') }}">로그아웃</a></p>
            <div class="school-portfolio">
                <h3>학력</h3>
                {
                    schools.map(school => {
                        return (
                            <div key={`school_${school.id}`}>
                                <p>{school.name}</p>
                                <p>{school.major}</p>
                                <div>
                                {school.status === 1 ? <p>재학중</p>
                                    : (school.status === 2 ? <p>학사졸업</p>
                                        : (school.status === 3 ? <p>석사졸업</p>
                                            : (school.status === 4 ? <p>박사졸업</p>
                                                : null
                                            )
                                        )
                                    )
                                }
                                </div>
                            </div>
                        )
                    })
                }
            </div>
            <div class="awards-portfolio">
                <h3>수상이력</h3>
                {
                    awards.map(award => {
                        return (
                            <div key={`award_${award.id}`}>
                                <p>{award.awards}</p>
                                <p>{award.awards_details}</p>
                            </div>
                        )
                    })
                }
            </div>
            <div class="projects-portfolio">
                <h3>프로젝트</h3>
                {
                    projects.map(project => {
                        return (
                            <div key={`project_${project.id}`}>
                                <p>{project.project_name}</p>
                                <p>{project.project_details}</p>
                            </div>
                        )
                    })
                }
            </div>
            <div class="certificates-portfolio">
                <h3>자격증</h3>
                {
                    certificates.map(certificate => {
                        return (
                            <div key={`certificate_${certificate.id}`}>
                                <p>{certificate.cert_name}</p>
                                <p>{certificate.cert_from}</p>
                                <p>{certificate.cert_date}</p>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    );
    
}

export default Main;
