import React, { useState } from 'react';
import axios from "axios";
import { useHistory } from 'react-router-dom';


const Signup = () => {
    // 입력값 저장할 state들
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [checkPassword, setCheckPassword] = useState("");
    const [name, setName] = useState("");

    // redirect 보내기
    const history = useHistory();
    // 백앤드로 데이터 전송 및 응답 받기
    const requestSignUp = async () => {
        try {
            const res = await axios.post(
                'http://localhost:1234/auth/signup',
                { email, password, checkPassword, name }
            );
            console.log(res.data)
            alert(res.data.message);
            history.push("/login");     
        } catch (e) {
            console.log(e);
        }
    }

    const handleSignUpSubmit = (e) => {
        e.preventDefault();
        requestSignUp();
    }


        return (
            <div class="text-center">
                <main class="form-signin">
                    <form onSubmit={e =>handleSignUpSubmit(e)}>
                        <h1 class="h3 mb-3 fw-normal">Please Sign up</h1>
                        <div class="form-floating">
                            <label htmlFor="user_id">아이디</label>
                            <input name="user_id" type="email" class="form-control" id="floatingInput" placeholder="name@example.com" onChange={e => setEmail(e.target.value)} />
                            <label for="floatingInput">Email address</label>
                        </div>
                        <div class="form-floating">
                            <label htmlFor="password">비밀번호</label>
                            <input type="password" class="form-control" id="floatingPassword" placeholder="Password" onChange={e => setPassword(e.target.value)} />
                            <label name="password" for="floatingPassword">Password</label>
                        </div>
                        <div class="form-floating">
                            <label htmlFor="password">비밀번호 확인</label>
                            <input name="check_password" type="password" class="form-control" id="floatingPassword" placeholder="Password" onChange={e => setCheckPassword(e.target.value)} />
                            <label for="floatingPassword">Check Password</label>
                        </div>
                        <div class="form-floating">
                            <label htmlFor="password">비밀번호</label>
                            <input name="name" type="name" class="form-control" id="floatingPassword" placeholder="이름 입력" onChange={e => setName(e.target.value)} />
                            <label for="floatingInput">Name</label>
                        </div>
                        <button class="w-100 btn btn-lg btn-secondary" type="submit">Sign up</button>
                        <p class="mt-5 mb-3 text-muted">&copy; 2021 RacerIn Co. all rights reserved.</p>
                    </form>
                </main>
            </div>
        );
}

export default Signup;
