import React, {useState} from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';


const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const requestLogin = async (email, password) => {
        try {
            await axios.post(
                "http://localhost:1234/login",
                { email, password }
            );
            history.push("/main");
        } catch (e) {
            console.log(e.response.data.message);
        }
    }

    const handleLoginSubmit = (e) => {
        e.preventDefault();
        requestLogin(email, password);
    }

    const history = useHistory();
        return (
            <div>
                <form onSubmit={handleLoginSubmit} method="post">
                    <div>
                        <label htmlFor="user_id">아이디</label>
                        <input type="text" name="user_id" id="user_id" placeholder="아이디 입력" onChange={e => setEmail(e.target.value)} />
                    </div>
                    <div>
                        <label htmlFor="password">비밀번호</label>
                        <input name="password" type="password" placeholder="비밀번호 입력" onChange={e => setPassword(e.target.value)} />
                    </div>
                    <input type="submit" value="로그인" />
                    <button class="nav-link" onClick={() => history.push("/signup")}>회원가입</button>
                </form>
            </div>
        );
}

export default Login;
