import React, {useState} from 'react';
import axios from "axios";


const EditSchools = ({ data, sessionId, handleEditSchools, isClicked, setIsClicked }) => {
    const name          = data.name;
    const major         = data.major;
    const status        = data.status;
    const school_id     = data.school_id;

    if (isClicked){
        handleEditSchools();
        setIsClicked();
        requestSaveSchool();
    }
    const requestSaveSchool = async () => {
        try {
            await axios.post(
                'http://localhost:1234/edit/school',
                { sessionId, name, major, status, school_id }
            );
        } catch (e) {
            console.log(e);
        }
    }

    return (
        <div key={`school_${data.id}`}>
            <main className="form-input">
                <div className="form-floating">
                    <input name="user_id" type="text" className="form-control" id="floatingInput" placeholder="학교이름" defaultValue={data.name} />
                    <label htmlFor="floatingInput">학교이름</label>
                </div>
                <div className="form-floating">
                    <input name="user_id" type="text" className="form-control" id="floatingInput" placeholder="전공" defaultValue={data.major} />
                    <label htmlFor="floatingInput">전공</label>
                </div>
                <div className="form-floating">
                    <input name="name" type="text" className="form-control" id="floatingPassword" placeholder="상태" defaultValue={data.status} />
                    <label htmlFor="floatingInput">상태</label>
                </div>
            </main>
        </div>
    );
    
}

const Schools = ({ sessionId, schoolData, editStatus, handleEditSchools }) => {
    const [isClicked, setIsClicked] = useState(false);
    const handleSchoolSubmit = (e) => {
        e.preventDefault();
        setIsClicked(prevState => !prevState.isClick);
    }
    return (
        <div>
            <div className="school-portfolio">
                <h3>학력</h3>
                <form onSubmit={(e) =>handleSchoolSubmit(e)}>
                    {
                        editStatus ?
                        Array.from(schoolData).map(school => {
                            return (
                                <EditSchools data={school} sessionId={sessionId} handleEditSchools={handleEditSchools} isClicked={isClicked} setIsClicked={setIsClicked} />
                            )
                        }) : 
                        Array.from(schoolData).map(school => {
                            return (
                                <div key={`school_${school.id}`}>
                                    <h5>{school.name}</h5>
                                    <h6>{school.major}</h6>
                                    <p>{school.status}</p>
                                </div>
                                
                            )
                        })
                    }
                    {
                        editStatus ?
                            <div>
                                <div className="btn-group" role="group" aria-label="Basic outlined example">
                                    <button className="btn btn-outline-primary" onClick={handleSchoolSubmit} type="submit">완료</button>
                                </div>
                            </div> :
                            <div className="btn-group" role="group" aria-label="Basic outlined example">
                                <button className="btn btn-outline-primary" onClick={handleEditSchools} type="button">수정</button>
                            </div>
                    }
                </form>
            </div>
        </div>
    );
    
}

export default Schools;
