import React, { useState, useEffect } from 'react';
import { Redirect } from 'react-router-dom';
import { FullPage } from '../styles/MainStyle';
import axios from "axios";

const ProfileCard = ({ data }) => {
    return (
        <div className="col">
            <div className="card">
                {/* <img src="..." className="card-img-top" alt="..." /> */}
                <div className="card-body">
                    <h5 className="card-title">{data.name}</h5>
                    <p className="card-text">{data.comments}</p>
                    <a href="#!" className="btn btn-primary">정보보기</a>
                </div>
            </div>
        </div>
    );
}

const Network = ({ sessionId }) => {
    const [serach, setSearch] = useState('');
    const [userData, setUserData] = useState('');
    useEffect(()=>{
        // Default roaded data
        const fetchUser = async () => {
            const res = await axios.post('http://localhost:1234/network/search_user', {serach, sessionId});
            const data = res.data.data;
            setUserData(data.users);
        }
        fetchUser();
    },[serach, sessionId, setUserData])

    return (
        <FullPage>
            {sessionId === null && <Redirect to="/login" />}
            <div>
                <div className="input-group mb-3">
                    <input type="text" className="form-control" placeholder="이름으로 검색" aria-label="Recipient's username" aria-describedby="button-addon2" onChange={e => setSearch(e.target.value)} />
                </div>
                {
                Array.from(userData).map(user => {
                    return (
                        <div key={`${user.id}_search`} className="row row-cols-1 row-cols-md-3 g-4">
                            <ProfileCard data={user} />
                        </div>
                    )
                })
            }
            </div>
        </FullPage>
    );
    
}

export default Network;
