import React, { useState, useEffect } from 'react';
import { Redirect } from 'react-router-dom';
import axios from "axios";
import styled from "styled-components";

import Awards from "./components/Awards";
import Schools from "./components/Schools";
import Projects from "./components/Projects";
import Certificates from "./components/Certificates";

const Total = styled.div`
  width: 100vw;
  height: 100vh;
`;
const LeftSide = styled.div`
  width: 30vw;
  height: 100vh;
  float: left;
`;
const RightSide = styled.div`
  width: 70vw;
  height: 100vh;
  display: table;
  float: right;
`;

const Main = ({ sessionId }) => {
    const [awardData, setAwardData] = useState({});
    const [schoolData, setSchoolData] = useState({});
    const [certificateData, setCertificateData] = useState({});
    const [projectData, setProjectsData] = useState({});

    useEffect(()=>{
        const fetchPortfolio = async () => {
            const res = await axios.post('http://localhost:1234/portfolio/main', {sessionId});
            const data = res.data.data.portfolio;

            setAwardData(data.awards);
            setSchoolData(data.schools);
            setCertificateData(data.certificates);
            setProjectsData(data.projects);
        }
        fetchPortfolio();
    }, [sessionId])

    return (
        <Total>
            {sessionId === null && <Redirect to="/login" />}
            <h1>메인페이지</h1>
            <RightSide>
                <Schools schoolData={schoolData} />
                <Awards awardData={awardData} />
                <Projects projectData={projectData} />
                <Certificates certificateData={certificateData} />
            </RightSide>
        </Total>
    );
    
}

export default Main;
