import React, { useState, useEffect } from 'react';
import axios from "axios";

const Main = () => {
    const [awardData, setAwardData] = useState({});
    const [schoolData, setSchoolData] = useState({});
    const [certificateData, setCertificateData] = useState({});
    const [projectData, setProjectsData] = useState({});
    // 임시로 설정
    window.sessionStorage.setItem("id", "aaa@gmail.com");
    const logoutRequest = async () => {
        try {
            await axios.get("http://localhost:1234/logout");
            window.sessionStorage.clear();
        }
        catch (e) {
            alert(e);
        }
    }
    useEffect(()=>{
        const fetchPortfolio = async () => {
            const res = await axios.get('http://localhost:1234/main');
            console.log(res);
            debugger;
            makePortfolio(res.data.portfolio);
        }

        const makePortfolio = (datas) => {
            const arr = datas.reduce((acc, cur) => {
                const awards = cur.awards;
                const schools = cur.schools;
                const certificates = cur.certificates;
                const projects = cur.projects;
                
                acc.push({awards, schools, certificates, projects})

                console.log(cur);
                return acc;

            }, [])

            setAwardData({data: arr.map(a=>a.awards)});
            setSchoolData({data: arr.map(a=>a.schools)});
            setCertificateData({data: arr.map(a=>a.certificates)});
            setProjectsData({data: arr.map(a=>a.projects)});
        }

        fetchPortfolio();
    }, [])
    return (
        <div>
            <h1>메인페이지</h1>
            <p><button onClick={() => {logoutRequest()}}>로그아웃</button></p>
            <div className="school-portfolio">
                <h3>학력</h3>
                {
                    Object.entries(schoolData).map(school => {
                        return (
                            <div key={`school_${school.id}`}>
                                <p>{school.name}</p>
                                <p>{school.major}</p>
                                <div>
                                {school.status === 1 ? <p>재학중</p>
                                    : (school.status === 2 ? <p>학사졸업</p>
                                        : (school.status === 3 ? <p>석사졸업</p>
                                            : (school.status === 4 ? <p>박사졸업</p>
                                                : null
                                            )
                                        )
                                    )
                                }
                                </div>
                            </div>
                        )
                    })
                }
            </div>
            <div className="awards-portfolio">
                <h3>수상이력</h3>
                {
                    Object.entries(awardData).map(award => {
                        return (
                            <div key={`award_${award.id}`}>
                                <p>{award.awards}</p>
                                <p>{award.awards_details}</p>
                            </div>
                        )
                    })
                }
            </div>
            <div className="projects-portfolio">
                <h3>프로젝트</h3>
                {
                    Object.entries(projectData).map(project => {
                        return (
                            <div key={`project_${project.id}`}>
                                <p>{project.project_name}</p>
                                <p>{project.project_details}</p>
                            </div>
                        )
                    })
                }
            </div>
            <div className="certificates-portfolio">
                <h3>자격증</h3>
                {
                    Object.entries(certificateData).map(certificate => {
                        return (
                            <div key={`certificate_${certificate.id}`}>
                                <p>{certificate.cert_name}</p>
                                <p>{certificate.cert_from}</p>
                                <p>{certificate.cert_date}</p>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    );
    
}

export default Main;
