import React, { useState } from 'react';
import axios from "axios";
import { useHistory } from 'react-router-dom';
export const API_BASE_URL = "http://localhost:1234";


const Signup = () => {
    // 입력값 저장할 state들
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [checkPassword, setCheckPassword] = useState("");
    const [name, setName] = useState("");

    // redirect 보내기
    const history = useHistory();
    // 백앤드로 데이터 전송 및 응답 받기
    const requestSignUp = async () => {
        try {
            const res = await axios.post(
                `${API_BASE_URL}/auth/signup`,
                { email, password, checkPassword, name }
            );
            console.log(res.data)
            alert(res.data.message);
            history.push("/login");     
        } catch (e) {
            console.log(e);
        }
    }

    const handleSignUpSubmit = (e) => {
        e.preventDefault();
        requestSignUp();
    }


        return (
            <section>
                <form onSubmit={e => handleSignUpSubmit(e)}>
                    <div className="mb-3">
                        <label htmlFor="user_id">아이디</label>
                        <input type="text" name="user_id" id="user_id" placeholder="이메일 입력" onChange={e => setEmail(e.target.value)} />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="password">비밀번호</label>
                        <input name="password" type="password" placeholder="비밀번호 입력" onChange={e => setPassword(e.target.value)} />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="password">비밀번호 확인</label>
                        <input name="check_password" type="password" placeholder="비밀번호 확인" onChange={e => setCheckPassword(e.target.value)} />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="name">이름</label>
                        <input name="name" type="name" placeholder="이름 입력" onChange={e => setName(e.target.value)} />
                    </div>
                    <button type="submit" className="btn btn-secondary">회원가입</button>

                </form>
            </section>
        );
}

export default Signup;
