import React, { useState, useEffect } from 'react';
import { Redirect } from 'react-router-dom';
import axios from "axios";

import Awards from "../components/portfolio/Awards";
import Schools from "../components/portfolio/Schools";
import Projects from "../components/portfolio/Projects";
import Certificates from "../components/portfolio/Certificates";
import Profile from "../components/portfolio/Profile";
import { FullPage, LeftSide, RightSide } from '../styles/MainStyle';

const Main = ({ sessionId }) => {
    const [awardData, setAwardData] = useState({});
    const [schoolData, setSchoolData] = useState({});
    const [certificateData, setCertificateData] = useState({});
    const [projectData, setProjectData] = useState({});
    const [profileData, setProfileData] = useState({});

    const [edit, setEdit] = useState({
        profile: false,
        school: false,
        award: false,
        project: false,
        certificate: false,
    });

    // get Data from Flask
    useEffect(()=>{
        const fetchPortfolio = async () => {
            const res = await axios.post('http://localhost:1234/portfolio/main', {sessionId});
            const data = res.data.data.portfolio;
            setAwardData(data.awards);
            setSchoolData(data.schools);
            setCertificateData(data.certificates);
            setProjectData(data.projects);
            setProfileData(data.profile);
        }
        fetchPortfolio();
    }, [sessionId])

    // Set the "Edit" State's status 
    const handleEditProfile = () => setEdit({profile : true});
    const handleEditAwards = () => setEdit({profile : true});
    const handleEditSchools = () => setEdit({profile : true});
    const handleEditCertificates = () => setEdit({profile : true});
    const handleEditProject = () => setEditMode({profile : true});

    const templateStyle = {
        width: '60vw'
    }

    return (
        
        <FullPage>
            {sessionId === null && <Redirect to="/login" />}
            <h1 className="text-center">My Protfolio</h1>
            <br />
            <LeftSide>
                <div className="card text-center" style={{width: '28vw'}}>
                    <div className="card-body">
                        <Profile profileData={profileData} />
                        <div className="btn-group" role="group" aria-label="Basic outlined example">
                            <button type="button" className="btn btn-outline-primary" onClick={handleEditProfile}>수정</button>
                        </div>
                    </div>
                </div>
            </LeftSide>
            <RightSide>
                <div className="card text-center" style={templateStyle}>
                    <div className="card-body">
                        <Schools schoolData={schoolData} />
                        <div className="btn-group" role="group" aria-label="Basic outlined example">
                            <button type="button" className="btn btn-outline-primary" onClick={handleEditSchools}>수정</button>
                            <button type="button" className="btn btn-outline-primary">삭제</button>
                        </div>
                    </div>
                </div>

                <div className="card text-center" style={templateStyle}>
                    <div className="card-body">
                        <Awards awardData={awardData} />
                        <div className="btn-group" role="group" aria-label="Basic outlined example">
                            <button type="button" className="btn btn-outline-primary" onClick={handleEditAwards}>수정</button>
                            <button type="button" className="btn btn-outline-primary">삭제</button>
                        </div>
                    </div>
                </div>

                <div className="card text-center" style={templateStyle}>
                    <div className="card-body">
                        <Projects projectData={projectData} />
                        <div className="btn-group" role="group" aria-label="Basic outlined example">
                            <button type="button" className="btn btn-outline-primary" onClick={handleEditProject}>수정</button>
                            <button type="button" className="btn btn-outline-primary">삭제</button>
                        </div>
                    </div>
                </div>

                <div className="card text-center" style={templateStyle}>
                    <div className="card-body">
                        <Certificates certificateData={certificateData} />
                        <div className="btn-group" role="group" aria-label="Basic outlined example">
                            <button type="button" className="btn btn-outline-primary" onClick={handleEditCertificates}>수정</button>
                            <button type="button" className="btn btn-outline-primary">삭제</button>
                        </div>
                    </div>
                </div>
            </RightSide>
        </FullPage>
    );
    
}

export default Main;
