import React from 'react';

const Schools = ({ schoolData, status }) => {

    return (
        <div>
            <div className="school-portfolio">
                <h3>학력</h3>
                {
                    status ?
                    Array.from(schoolData).map(school => {
                        return (
                            <div key={`school_${school.id}`}>
                                <input type="text" name="name" placeholder="학교이름" defaultValue={education.name} />
                                <input type="text" name="major" placeholder="전공" defaultValue={education.major} />
                                <div className="form-check form-check-inline" name="status">
                                    <input className="form-check-input" type="radio" name="inlineRadioOptions" id="inlineRadio1" value="1" />
                                    <label className="form-check-label" htmlFor="inlineRadio1">재학중</label>
                                </div>
                                <div className="form-check form-check-inline">
                                    <input className="form-check-input" type="radio" name="inlineRadioOptions" id="inlineRadio2" value="2" />
                                    <label className="form-check-label" htmlFor="inlineRadio2">학사졸업</label>
                                </div>
                                <div className="form-check form-check-inline">
                                    <input className="form-check-input" type="radio" name="inlineRadioOptions" id="inlineRadio3" value="3" />
                                    <label className="form-check-label" htmlFor="inlineRadio3">석사졸업</label>
                                </div>
                                <div className="form-check form-check-inline">
                                    <input className="form-check-input" type="radio" name="inlineRadioOptions" id="inlineRadio4" value="4" />
                                    <label className="form-check-label" htmlFor="inlineRadio4">박사졸업</label>
                                </div>
                            </div>
                        )
                    }) : 
                    Array.from(schoolData).map(school => {
                        return (
                            <div key={`school_${school.id}`}>
                                <h5>{school.name}</h5>
                                <h6>{school.major}</h6>
                                <p>{school.status}</p>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    );
    
}

export default Schools;
